import React, { useState, useRef } from 'react';
// import { Map, TileLayer } from 'react-leaflet';
import { MapContainer, TileLayer } from 'react-leaflet';
// import Select, { Creatable } from 'react-select';
import Select from 'react-select';
// import { CreatableSelect } from 'react-select';
import Creatable from 'react-select/creatable';
import './App.css';
import 'leaflet/dist/leaflet.css';

const defaultCenter = [39.8283, -98.5795]; // Geographical center of the continental USA
const defaultZoom = 4;

const usStates = [
  { value: 'AL', label: 'Alabama' },
  { value: 'AK', label: 'Alaska' },
  { value: 'AZ', label: 'Arizona' },
  { value: 'AR', label: 'Arkansas' },
  { value: 'CA', label: 'California' },
  { value: 'CO', label: 'Colorado' },
  { value: 'CT', label: 'Connecticut' },
  { value: 'DE', label: 'Delaware' },
  { value: 'FL', label: 'Florida' },
  { value: 'GA', label: 'Georgia' },
  { value: 'HI', label: 'Hawaii' },
  { value: 'ID', label: 'Idaho' },
  { value: 'IL', label: 'Illinois' },
  { value: 'IN', label: 'Indiana' },
  { value: 'IA', label: 'Iowa' },
  { value: 'KS', label: 'Kansas' },
  { value: 'KY', label: 'Kentucky' },
  { value: 'LA', label: 'Louisiana' },
  { value: 'ME', label: 'Maine' },
  { value: 'MD', label: 'Maryland' },
  { value: 'MA', label: 'Massachusetts' },
  { value: 'MI', label: 'Michigan' },
  { value: 'MN', label: 'Minnesota' },
  { value: 'MS', label: 'Mississippi' },
  { value: 'MO', label: 'Missouri' },
  { value: 'MT', label: 'Montana' },
  { value: 'NE', label: 'Nebraska' },
  { value: 'NV', label: 'Nevada' },
  { value: 'NH', label: 'New Hampshire' },
  { value: 'NJ', label: 'New Jersey' },
  { value: 'NM', label: 'New Mexico' },
  { value: 'NY', label: 'New York' },
  { value: 'NC', label: 'North Carolina' },
  { value: 'ND', label: 'North Dakota' },
  { value: 'OH', label: 'Ohio' },
  { value: 'OK', label: 'Oklahoma' },
  { value: 'OR', label: 'Oregon' },
  { value: 'PA', label: 'Pennsylvania' },
  { value: 'RI', label: 'Rhode Island' },
  { value: 'SC', label: 'South Carolina' },
  { value: 'SD', label: 'South Dakota' },
  { value: 'TN', label: 'Tennessee' },
  { value: 'TX', label: 'Texas' },
  { value: 'UT', label: 'Utah' },
  { value: 'VT', label: 'Vermont' },
  { value: 'VA', label: 'Virginia' },
  { value: 'WA', label: 'Washington' },
  { value: 'WV', label: 'West Virginia' },
  { value: 'WI', label: 'Wisconsin' },
  { value: 'WY', label: 'Wyoming' }
];


function App() {
  const mapRef = useRef();
  const [selectedStates, setSelectedStates] = useState([]);
  const [selectedCities, setSelectedCities] = useState([]);
  const [selectedCounties, setSelectedCounties] = useState([]);
  // const [selectedState, setSelectedState] = useState(null);
  const [dateRange] = useState({ startDate: '', endDate: '' });
  const [showTraffic] = useState(true);
  const [showConstruction] = useState(true);
  const [realTimeData] = useState(false);
  const [accidentType, setAccidentType] = useState('');
  const [constructionType, setConstructionType] = useState('');
  const [geographicArea, setGeographicArea] = useState('');
  const [timeOfDay, setTimeOfDay] = useState('');
  const imageUrls = [
    'images/download.jpeg',
    'images/download.jpeg',
    'images/download.jpeg',
    'images/download.jpeg',

  ];
  const [toggleState, setToggleState] = useState('option1'); // 'option1', 'option2', or 'option3'
  const handleToggleChange = (event) => {
    setToggleState(event.target.value);
  };

  const handleStateChange = selectedOption => {
      setSelectedStates(selectedOption);
    // Additional logic when a state is selected can be added here
  };

  const handleCityChange = selectedOptions => {
    setSelectedCities(selectedOptions);
  };

  const handleCountyChange = selectedOptions => {
    setSelectedCounties(selectedOptions);
  };

  return (
    <div className="App">
      {/* <Map ref={mapRef} center={defaultCenter} zoom={defaultZoom} className="map">
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />
      </Map> */}
      <div className = "content">
        <MapContainer ref={mapRef} center={defaultCenter} zoom={defaultZoom} className="map">
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />
        </MapContainer>

      <div className="sidebar">
        <h2>Filter Panel</h2>
        {/* Existing filters... */}
        {/* New filters added below */}
        <div className="filter-option">
  <label>Choose Dataset :</label>
  <div className="three-way-toggle">
    <input
      type="radio"
      id="toggleOption1"
      name="toggle"
      value="option1"
      checked={toggleState === 'option1'}
      onChange={handleToggleChange}
    />
    <label htmlFor="toggleOption1">Constructions</label>

    <input
      type="radio"
      id="toggleOption2"
      name="toggle"
      value="option2"
      checked={toggleState === 'option2'}
      onChange={handleToggleChange}
    />
    <label htmlFor="toggleOption2">Accidents</label>

    <input
      type="radio"
      id="toggleOption3"
      name="toggle"
      value="option3"
      checked={toggleState === 'option3'}
      onChange={handleToggleChange}
    />
    <label htmlFor="toggleOption3">Co-relation</label>
  </div>
</div>


        <div className="filter-option">
          <label htmlFor="state-select">Select State:</label>
          <Select
            id="state-select"
            value={selectedStates}
            onChange={handleStateChange}
            options={usStates}
            isMulti={true}
            isClearable={true}
            isSearchable={true}
            closeMenuOnSelect={false}
            classNamePrefix="select"
            placeholder="Search and select states..."
          />
        </div>

        <div className="filter-option">
          <label htmlFor="city-select">Enter City(s):</label>
          <Creatable
            id="city-select"
            isMulti
            onChange={handleCityChange}
            value={selectedCities}
            placeholder="Type and press enter..."
            classNamePrefix="select"
            menuIsOpen={false}
          />
        </div>

        <div className="filter-option">
          <label htmlFor="county-select">Enter County(s):</label>
          <Creatable
            id="county-select"
            isMulti
            onChange={handleCountyChange}
            value={selectedCounties}
            placeholder="Type and press enter..."
            classNamePrefix="select"
            menuIsOpen={false}
          />
        </div>


        <div className="date-picker">
          <label htmlFor="constructionType">Date Range Selection</label>
          <label htmlFor="constructionType"> </label>
          <label htmlFor="startDate"> Start Date:</label>
          <input
            type="date"
            id="startDate"
            name="startDate"
            value={dateRange.startDate}
          />
        </div>
        <div className="date-picker">
          <label htmlFor="endDate">End Date:</label>
          <input
            type="date"
            id="endDate"
            name="endDate"
            value={dateRange.endDate}
          />
        </div>

        <button className="generate-report-btn">Generate Report</button>
      </div>
      </div>

      <div className="analysis-section">
        <h2>Analysis</h2>
        <div className="analysis-images">
          {imageUrls.map((url, index) => (
            <img key={index} src={process.env.PUBLIC_URL + '/' + url} alt={`Analysis ${index + 1}`} />
          ))}
        </div>
      </div>

    </div>


  );
}

export default App;
